Data.data:
.headband {
  display: none;
}
.custom-logo-image {
  background: #fff;
  max-width: initial;
  height: 55px;
}
.main-menu a {
  font-size: 18px;
}
.main-menu i {
  color: #f56627;
  font-size: 22px;
}
.tag-cloud-title {
  display: none;
}
@-webkit-keyframes bounce {
  10% {
    transform: translateY(2px);
  }
  80% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(-6px);
  }
}
.tag-cloud-tags a {
  border-radius: 15px;
  border: 3.5px solid #68aca3;
  padding: 10px 30px;
  width: 100px;
  transition: all 0.2s linear;
}
.tag-cloud-tags a:hover {
  border: inset 4.5px #f56627;
  animation: bounce 0.3s 1 normal ease-out forwards;
  -webkit-animation: bounce 0.3s 1 normal ease-out forwards;
  box-shadow: 0px 1px 3px 0px #f56627;
  opacity: 0.7;
}
.collection-title .collection-header {
  font-weight: normal;
  color: #f56627;
}
.collection-title .collection-header small {
  font-weight: bold;
  color: #fff;
}
.collection-header a {
  padding: 0px 14px 3px 8px;
  background-color: #62d1f8;
  border: 4px solid #fff;
  border-radius: 30px;
  transition: all 0.3s linear;
}
.collection-header a:hover {
  background-color: #ed5cad;
  box-shadow: inset -80px 0px 0px 5px rgba(255,255,255,0.6);
}
@-moz-keyframes bounce {
  10% {
    transform: translateY(2px);
  }
  80% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(-6px);
  }
}
@-webkit-keyframes bounce {
  10% {
    transform: translateY(2px);
  }
  80% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(-6px);
  }
}
@-o-keyframes bounce {
  10% {
    transform: translateY(2px);
  }
  80% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(-6px);
  }
}
@keyframes bounce {
  10% {
    transform: translateY(2px);
  }
  80% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(-6px);
  }
}

